mapper.select:
SELECT ba.board_no, ba.attachment_title, ba.attachment_writer, ba.attachment_content, bb.board_reg_date, bb.board_mod_date, bb.board_view, bb.category_board
FROM board_attachment ba LEFT JOIN base_board bb ON ba.board_no = bb.board_no
WHERE ba.board_no = #{boardNo} ;

mapper.update:
UPDATE
    board_attachment

SET
    attachment_title = #{boardTitle},
    attachment_writer = #{boardWriter},
    attachment_content = #{boardContent}

WHERE
    board_no = #{boardNo}
;
