mapper.select:
SELECT COUNT(ba.board_no)
FROM board_attachment ba LEFT JOIN base_board bb ON bb.board_no = ba.board_no
;

SELECT ba.board_no AS boardNo, ba.attachment_title AS boardTitle, ba.attachment_writer AS boardWriter, ba.attachment_content AS boardContent, bb.board_reg_date AS boardRegDate, bb.board_mod_date AS boardModDate, bb.board_view AS boardView, bb.category_board AS categoryBoard
FROM board_attachment ba LEFT JOIN base_board bb ON ba.board_no = bb.board_no
WHERE ba.board_no = #{boardNo} ;

mapper.update:
UPDATE
    board_attachment

SET
    attachment_title = #{boardTitle},
    attachment_writer = #{boardWriter},
    attachment_content = #{boardContent}

WHERE
    board_no = #{boardNo}
;
