mapper.select:
SELECT COUNT(bf.board_no)
FROM board_free bf LEFT JOIN base_board bb ON bb.board_no = bf.board_no
;

SELECT bf.board_no AS boardNo, bf.free_title AS boardTitle, bf.free_writer AS boardWriter, bf.free_content AS boardContent, bb.board_reg_date AS boardRegDate, bb.board_mod_date AS boardModDate, bb.board_view AS boardView, bb.category_board AS categoryBoard
FROM board_free bf LEFT JOIN base_board bb ON bf.board_no = bb.board_no
WHERE bf.board_no = #{boardNo} ;

mapper.update:
UPDATE
    board_free

SET
    free_title = #{boardTitle},
    free_writer = #{boardWriter},
    free_content = #{boardContent}

WHERE
    board_no = #{boardNo}
;
