mapper.select:
SELECT bf.board_no, bf.board_title, bf.board_writer, bf.board_content, bb.board_reg_date, bb.board_mod_date, bb.board_view, bb.category_board
FROM board_free bf LEFT JOIN base_board bb ON bf.board_no = bb.board_no
WHERE bf.board_no = #{boardNo} ;

mapper.update:
UPDATE
    board_free

SET
    board_title = #{boardTitle},
    board_writer = #{boardWriter},
    board_content = #{boardContent}

WHERE
    board_no = #{boardNo}
;
