mapper.select:
SELECT bn.board_no, bn.notice_title, bn.notice_writer, bn.notice_content, bb.board_reg_date, bb.board_mod_date, bb.category_board
FROM board_notice bn LEFT JOIN base_board bb ON bb.board_no = bn.board_no
WHERE bn.board_no = #{boardNo} ;

mapper.update:
UPDATE
    board_notice

SET
    notice_title = #{boardTitle},
    notice_writer = #{boardWriter},
    notice_content = #{boardContent}

WHERE
    board_no = #{boardNo}
;
