mapper.select:
SELECT *
FROM board_notice bn LEFT JOIN base_board bb ON bb.board_no = bn.board_no
WHERE bn.board_no = #{boardNo} ;

mapper.update:
UPDATE
    board_notice

SET
    board_title = #{boardTitle},
    board_writer = #{boardWriter},
    board_content = #{boardContent}

WHERE
    board_no = #{boardNo}
;
