mapper.select:
SELECT COUNT(bn.board_no)
FROM board_notice bn LEFT JOIN base_board bb ON bb.board_no = bn.board_no
;

SELECT bn.board_no AS boardNo, bn.notice_title AS boardTitle, bn.notice_writer AS boardWriter, bn.notice_content AS boardContent, bb.board_reg_date AS boardRegDate, bb.board_mod_date AS boardModDate, bb.board_view AS boardView, bb.category_board AS categoryBoard
FROM board_notice bn LEFT JOIN base_board bb ON bb.board_no = bn.board_no
WHERE bn.board_no = #{boardNo} ;

mapper.update:
UPDATE
    board_notice

SET
    notice_title = #{boardTitle},
    notice_writer = #{boardWriter},
    notice_content = #{boardContent}

WHERE
    board_no = #{boardNo}
;
