mapper.select:
SELECT reply_no, reply_content, reply_writer, reply_reg_date, board_no, parent_reply_no
FROM reply
WHERE board_no = #{boardNo}
ORDER BY reply_reg_date DESC ;

mapper.update:
UPDATE
    v_reply_blind_tbl

SET
    reply_content = #{replyContent}

WHERE
    reply_no = #{replyNo}
;

UPDATE v_reply_blind_tbl
SET reply_blind = 0
WHERE reply_no = #{replyNo} ;
